import React from "react";
import { Modal, Button } from "react-bootstrap";

function FriendOptionsPopup({
    show,
    onClose,
    onDeleteFriend,
    onAddToGroup,
    selectedFriend,
}) {
    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Header closeButton>
                <Modal.Title>Friend Options</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Button variant="danger" onClick={onDeleteFriend}>
                    Delete Friend
                </Button>
                <Button variant="primary" onClick={onAddToGroup}>
                    Add to Group
                </Button>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onClose}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
}

export default FriendOptionsPopup;
