import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { Badge, Row, Col, Button } from "react-bootstrap";

import "../../styles/screens/GroupsScreens.css";
import Message from "../Message";
import AlertMessage from "../AlertMessage";
import { getGroupExpenses } from "../../actions/groupActions";

function ExpenseList() {
    // group id
    const { id } = useParams();
    const dispatch = useDispatch();

    const groupExpenses = useSelector((state) => state.groupExpenses);
    const {
        error: groupExpensesError,
        loading: groupExpensesLoading,
        groupExpensesList,
    } = groupExpenses;

    // Define state to keep track of which rows are expanded
    const [expandedRows, setExpandedRows] = useState({});

    const toggleRowExpansion = (expenseId) => {
        setExpandedRows((prevState) => ({
            ...prevState,
            [expenseId]: !prevState[expenseId],
        }));
    };

    useEffect(() => {
        dispatch(getGroupExpenses(id));
    }, [dispatch, id]);

    return (
        <div className="expense-list">
            {groupExpensesLoading ? (
                <div>Loading...</div>
            ) : groupExpensesError ? (
                <Message variant="danger">{groupExpensesError}</Message>
            ) : groupExpensesList.length === 0 ? (
                <p>No expenses</p>
            ) : (
                <div className="">
                    <Row className="d-flex flex-row justify-content-center py-3">
                        <Col
                            xs={2}
                            sm={2}
                            md={1}
                            className="expense-list-col d-flex flex-row align-items-center justify-content-center"
                        >
                            Type
                        </Col>
                        <Col
                            xs={2}
                            sm={2}
                            md={1}
                            className="expense-list-col d-flex flex-row align-items-center"
                        >
                            <span>Payer</span>
                        </Col>
                        <Col
                            xs={3}
                            sm={4}
                            md={5}
                            className="expense-list-col d-flex flex-row align-items-center"
                        >
                            <span>Description</span>
                        </Col>
                        <Col
                            xs={2}
                            sm={2}
                            md={2}
                            className="expense-list-col d-flex flex-row align-items-center "
                        >
                            <span>Amount</span>
                        </Col>
                        <Col
                            xs={3}
                            sm={1}
                            md={2}
                            className="expense-list-col d-flex flex-row align-items-center "
                        >
                            <span>Date</span>
                        </Col>
                        <Col
                            xs={4}
                            sm={1}
                            md={1}
                            className="expense-list-col d-flex flex-row align-items-center justify-content-center py-2"
                        >
                            <div></div>
                        </Col>
                    </Row>
                    {groupExpensesList.map((expense) => (
                        <div key={expense.id} className="expense-row">
                            <Row
                                onClick={() => toggleRowExpansion(expense.id)}
                                style={{ cursor: "pointer" }}
                                className="d-flex flex-row justify-content-center p-0 border-top border-primary py-2 "
                            >
                                <Col
                                    xs={2}
                                    sm={2}
                                    md={1}
                                    className="expense-list-col d-flex flex-row align-items-center justify-content-center"
                                >
                                    <Badge pill bg="primary">
                                        Expense
                                    </Badge>
                                </Col>
                                <Col
                                    xs={2}
                                    sm={2}
                                    md={1}
                                    className="expense-list-col d-flex flex-row align-items-center"
                                >
                                    <span>{expense.payer.name}</span>
                                </Col>
                                <Col
                                    xs={3}
                                    sm={4}
                                    md={5}
                                    className="expense-list-col d-flex flex-row align-items-center"
                                >
                                    <span>{expense.description}</span>
                                </Col>
                                <Col
                                    xs={2}
                                    sm={2}
                                    md={2}
                                    className="expense-list-col d-flex flex-row align-items-center "
                                >
                                    <span>${expense.amount}</span>
                                </Col>
                                <Col
                                    xs={3}
                                    sm={1}
                                    md={2}
                                    className="expense-list-col d-flex flex-row align-items-center "
                                >
                                    <span>
                                        {new Date(
                                            expense.date
                                        ).toLocaleDateString()}
                                    </span>
                                </Col>
                                <Col
                                    xs={4}
                                    sm={1}
                                    md={1}
                                    className="expense-list-col d-flex flex-row align-items-center justify-content-center py-2"
                                >
                                    {expandedRows[expense.id] ? (
                                        <i className="fa-solid fa-angles-up"></i>
                                    ) : (
                                        <i className="fa-solid fa-angles-down"></i>
                                    )}
                                </Col>
                            </Row>

                            {/* Render the expanded row */}
                            {expandedRows[expense.id] && (
                                <Row className="expanded-row">
                                    <Col xs={6}>
                                        <div>
                                            <span>Payer:</span>{" "}
                                            {expense.payer.name}
                                        </div>
                                        <div>
                                            <span>Amount Paid:</span>{" "}
                                            {expense.amount}
                                        </div>
                                    </Col>
                                    <Col xs={6}>
                                        <h5>Expense Details</h5>
                                        {expense.expense_details.map(
                                            (detail) => (
                                                <div key={detail.user.id}>
                                                    <span>
                                                        {detail.user.name}
                                                    </span>{" "}
                                                    owes {detail.amount_owed}
                                                </div>
                                            )
                                        )}
                                    </Col>
                                </Row>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ExpenseList;
