import React, { useEffect, useState } from "react";
import { Modal, Button, FormControl, ListGroup } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

import { getUserFriendSearch } from "../actions/userActions";

function SearchFriendPopup({ show, onClose }) {
    const dispatch = useDispatch();
    const [searchTerm, setSearchTerm] = useState("");

    const userFriendSearchResults = useSelector(
        (state) => state.userFriendSearch
    );
    const { error, loading, userFriendSearch } = userFriendSearchResults;
    console.log(userFriendSearch);

    const handleClose = () => {
        setSearchTerm("");
        onClose();
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    useEffect(() => {
        dispatch(getUserFriendSearch(searchTerm));

        // const results = simulateSearchResults(searchTerm);
        // setSearchResults(results);
    }, [dispatch, searchTerm]);

    // const [searchResults, setSearchResults] = useState([]);
    // // Simulate search results for demonstration purposes
    // const simulateSearchResults = (term) => {
    //     // Replace this with actual API call to get search results
    //     // For now, return a dummy list of results
    //     return [
    //         { id: 1, name: "User 1" },
    //         { id: 2, name: "User 2" },
    //         // ... more search results
    //     ];
    // };

    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Header closeButton>
                <Modal.Title>Search Friends</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <FormControl
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="mb-1"
                />
                <ListGroup>
                    {userFriendSearch.map((friend) => (
                        <ListGroup.Item key={friend.id}>
                            {friend.email}
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
}

export default SearchFriendPopup;
