import React, { useState } from "react";
import { Modal, Button, Form, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

function SettlePopup({ receivingUser, show, handleClose }) {
    const [amount, setAmount] = useState("");

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const handleSubmit = () => {
        // Handle submission, e.g., dispatch an action to settle the debt
        console.log("Hello, submitted:", amount);
        handleClose(); // Close the modal
    };

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Settle Debt</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="border border-primary text-center">
                    <h3>
                        {userInfo.name} ={">"} {receivingUser.name}
                    </h3>
                </div>
                <Form.Group controlId="formAmount">
                    <Form.Control
                        type="number"
                        placeholder="Enter amount"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                    />
                </Form.Group>
            </Modal.Body>
            <Modal.Footer>
                <Row className="d-flex flex-row justify-content-between border border-primary">
                    <Button variant="primary" onClick={handleSubmit}>
                        Submit
                    </Button>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Row>
            </Modal.Footer>
        </Modal>
    );
}

export default SettlePopup;
