import React, { useState } from "react";
import { Dropdown, DropdownButton } from "react-bootstrap";

const credits = {
    credits_per_user: {
        1: [
            {
                debtor: {
                    id: 11,
                    username: "david@email.com",
                    email: "david@email.com",
                    name: "david",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "598.32",
            },
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "297.36",
            },
        ],
        3: [
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "150.96",
            },
            {
                debtor: {
                    id: 9,
                    username: "bob@email.com",
                    email: "bob@email.com",
                    name: "bob",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "280.32",
            },
            {
                debtor: {
                    id: 12,
                    username: "ema@email.com",
                    email: "ema@email.com",
                    name: "ema",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "173.32",
            },
        ],
    },
};

const debts = {
    debts_per_user: {
        11: [
            {
                debtor: {
                    id: 11,
                    username: "david@email.com",
                    email: "david@email.com",
                    name: "david",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "598.32",
            },
        ],
        10: [
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "297.36",
            },
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "150.96",
            },
        ],
        9: [
            {
                debtor: {
                    id: 9,
                    username: "bob@email.com",
                    email: "bob@email.com",
                    name: "bob",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "280.32",
            },
        ],
        12: [
            {
                debtor: {
                    id: 12,
                    username: "ema@email.com",
                    email: "ema@email.com",
                    name: "ema",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "173.32",
            },
        ],
    },
};

function GroupCreditDebit({ groupDetails }) {
    const [selectedUser, setSelectedUser] = useState(null);

    // Function to handle user selection
    const handleUserSelect = (userId) => {
        setSelectedUser(userId);
    };

    // Function to render debt and credit details
    const renderDebtCreditDetails = () => {
        if (selectedUser) {
            const userDebts = debts.debts_per_user[selectedUser] || [];
            const userCredits = credits.credits_per_user[selectedUser] || [];

            return (
                <div>
                    <h4>User Details</h4>
                    <p>
                        Name:{" "}
                        {
                            groupDetails.members.find(
                                (m) => m.id === selectedUser
                            ).name
                        }
                    </p>
                    <h5>Debts:</h5>
                    <ul>
                        {userDebts.map((debt, index) => (
                            <li key={index}>
                                Owes {debt.creditor.name} ${debt.amount}
                            </li>
                        ))}
                    </ul>
                    <h5>Credits:</h5>
                    <ul>
                        {userCredits.map((credit, index) => (
                            <li key={index}>
                                Owed by {credit.debtor.name} ${credit.amount}
                            </li>
                        ))}
                    </ul>
                </div>
            );
        }
        return null;
    };

    return (
        <div>
            <h2>Group Credit/Debit</h2>
            <div className="user-list">
                <h4>Group Members:</h4>
                <ul>
                    {groupDetails.members.map((member) => (
                        <li key={member.id}>
                            <DropdownButton
                                id={`dropdown-user-${member.id}`}
                                title={member.name}
                                onSelect={() => handleUserSelect(member.id)}
                            >
                                {groupDetails.members.map((otherMember) => (
                                    <Dropdown.Item
                                        key={otherMember.id}
                                        eventKey={otherMember.id}
                                    >
                                        {otherMember.name}
                                    </Dropdown.Item>
                                ))}
                            </DropdownButton>
                        </li>
                    ))}
                </ul>
            </div>
            <div className="user-details">{renderDebtCreditDetails()}</div>
        </div>
    );
}

export default GroupCreditDebit;
