import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { useDispatch } from "react-redux";

/**
 * only show delete button if user is user who created the group
 *
 * delete group api
 * actions and reducers for delete group
 *
 * success alert message in GroupList (refresh data when that happens)
 */

function DeleteGroupConfirmationPopup({ show, onClose, groupName, groupId }) {
    const handleConfirmDelete = () => {
        console.log("deleting groupid: ", groupId, "group name: ", groupName);
    };

    return (
        <div>
            <Modal show={show} onHide={onClose}>
                <Modal.Header>
                    <Modal.Title>Delete Group</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div>Are you sure you want to delete?</div>
                    <div className="d-flex flex-row justify-content-between">
                        <Button variant="primary" onClick={handleConfirmDelete}>
                            Delete
                        </Button>
                        <Button variant="secondary" onClick={() => onClose()}>
                            Cancel
                        </Button>
                    </div>
                </Modal.Body>
            </Modal>
        </div>
    );
}

export default DeleteGroupConfirmationPopup;
