import React from "react";
import { Modal, Button } from "react-bootstrap";

function ExpenseDetailPopup({ show, onClose, expense }) {
    if (!expense) {
        return null;
    }

    return (
        <div>
            <Modal show={show} onHide={onClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Expense Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div>
                        <span>Payer:</span> {expense.payer.name}
                    </div>
                    <div>
                        <span>Amount Paid:</span> {expense.amount}
                    </div>
                    <h5>Expense Details</h5>
                    {expense.expense_details.map((detail) => (
                        <div key={detail.user.id}>
                            <span>{detail.user.name}</span> owes{" "}
                            {detail.amount_owed}
                        </div>
                    ))}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default ExpenseDetailPopup;
