import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Card } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

import { getGroupsList } from "../../actions/groupActions";

function GroupsList() {
    const dispatch = useDispatch();

    const groupLists = useSelector((state) => state.groupLists);
    const { error, loading, groupList } = groupLists;

    useEffect(() => {
        dispatch(getGroupsList());
    }, [dispatch]);
    return (
        <div>
            {groupList.map((group) => (
                <div className="border border-primary" key={group.id}>
                    <Link
                        to={`/groups/${group.id}`}
                        className="text-decoration-none"
                    >
                        <Card className="mb-3" style={{ cursor: "pointer" }}>
                            <Card.Body>
                                <h5>{group.name}</h5>
                                <div className="mb-2">
                                    <strong>Last Two Transactions:</strong>
                                    {/* {group.transactions
                                        .slice(-2)
                                        .map((transaction) => (
                                            <div key={transaction.id}>
                                                {transaction.description}: $
                                                {transaction.amount}
                                            </div>
                                        ))} */}
                                </div>
                                <div>
                                    <strong>
                                        You are owed this money
                                        {/* {group.oweYou > 0
                                            ? `You are owed: $${group.oweYou}`
                                            : `You owe: $${-group.youOwe}`} */}
                                    </strong>
                                </div>
                            </Card.Body>
                        </Card>
                    </Link>
                </div>
            ))}
        </div>
    );
}

export default GroupsList;
