import React, { useState } from "react";
import { Modal, Button, FormControl } from "react-bootstrap";

function UpdateNamePopup({ show, onClose, currentGroupName }) {
    const [groupName, setGroupName] = useState(currentGroupName);

    const handleClose = () => {
        onClose();
    };

    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Header>
                <Modal.Title>Search Users</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <FormControl
                    type="text"
                    placeholder="Search..."
                    value={groupName}
                    onChange={() => setGroupName(e.target.value)}
                    className="mb-3"
                />
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
}

export default UpdateNamePopup;
