import React from "react";
import { Card } from "react-bootstrap";

function GroupsOweUser({ groups }) {
    return (
        <div className="groups-owe-you col-md-4">
            <h3>Groups that owe you</h3>
            {groups.map((group) => (
                <div className="border border-primary">
                    <Card className="mb-3" style={{ cursor: "pointer" }}>
                        <Card.Body>
                            <h5>{group.name}</h5>
                            <div className="mb-2">
                                <strong>Last Two Transactions:</strong>
                                {group.transactions
                                    .slice(-2)
                                    .map((transaction) => (
                                        <div key={transaction.id}>
                                            {transaction.description}: $
                                            {transaction.amount}
                                        </div>
                                    ))}
                            </div>
                            <div>
                                <strong>
                                    {group.oweYou > 0
                                        ? `You are owed: $${group.oweYou}`
                                        : `You owe: $${-group.youOwe}`}
                                </strong>
                            </div>
                        </Card.Body>
                    </Card>
                </div>
            ))}
        </div>
    );
}

export default GroupsOweUser;
