import React, { useState, useEffect } from "react";
import { Accordion, ListGroup } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

import Message from "../Message";
import { getGroupCredits, getGroupDebts } from "../../actions/groupActions";

const credits = {
    credits_per_user: {
        1: [
            {
                debtor: {
                    id: 11,
                    username: "david@email.com",
                    email: "david@email.com",
                    name: "david",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "598.32",
            },
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "297.36",
            },
        ],
        3: [
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "150.96",
            },
            {
                debtor: {
                    id: 9,
                    username: "bob@email.com",
                    email: "bob@email.com",
                    name: "bob",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "280.32",
            },
            {
                debtor: {
                    id: 12,
                    username: "ema@email.com",
                    email: "ema@email.com",
                    name: "ema",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "173.32",
            },
        ],
    },
};

const debts = {
    debts_per_user: {
        11: [
            {
                debtor: {
                    id: 11,
                    username: "david@email.com",
                    email: "david@email.com",
                    name: "david",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "598.32",
            },
        ],
        10: [
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 1,
                    username: "jeus@email.com",
                    email: "jeus@email.com",
                    name: "Jeus",
                },
                group: 6,
                amount: "297.36",
            },
            {
                debtor: {
                    id: 10,
                    username: "charlie@email.com",
                    email: "charlie@email.com",
                    name: "charlie",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "150.96",
            },
        ],
        9: [
            {
                debtor: {
                    id: 9,
                    username: "bob@email.com",
                    email: "bob@email.com",
                    name: "bob",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "280.32",
            },
        ],
        12: [
            {
                debtor: {
                    id: 12,
                    username: "ema@email.com",
                    email: "ema@email.com",
                    name: "ema",
                },
                creditor: {
                    id: 3,
                    username: "tim@email.com",
                    email: "tim@email.com",
                    name: "tim",
                },
                group: 6,
                amount: "173.32",
            },
        ],
    },
};
/**
 * ADD ACTIONS AND REDUCERS TO GET CREDITS AND DEBTS DATA
 */

function GroupCreditDebit({ groupDetails }) {
    const dispatch = useDispatch();

    const groupCredits = useSelector((state) => state.groupCredits);
    const {
        error: groupCreditError,
        loading: groupCreditLoading,
        groupCredit,
    } = groupCredits;

    console.log(groupCredit);

    const groupDebts = useSelector((state) => state.groupDebts);
    const {
        error: groupDebtError,
        loading: groupDebtLoading,
        groupDebt,
    } = groupDebts;

    useEffect(() => {
        dispatch(getGroupCredits(groupDetails.id));
        dispatch(getGroupDebts(groupDetails.id));
    }, [dispatch, groupDetails]);

    return (
        <div>
            {groupCreditLoading || groupDebtLoading ? (
                <div>Loading...</div>
            ) : groupCreditError ? (
                <Message variant="danger">{groupCreditError}</Message>
            ) : groupDebtError ? (
                <Message variant="danger">{groupDebtError}</Message>
            ) : (
                <div>
                    <h4>Members</h4>

                    <Accordion defaultActiveKey={null}>
                        {groupDetails.members.map((member) => (
                            <Accordion.Item
                                eventKey={member.id}
                                key={member.id}
                            >
                                <Accordion.Header>
                                    {member.name}
                                </Accordion.Header>
                                <Accordion.Body>
                                    {groupCredit.credits_per_user[
                                        member.id
                                    ] && (
                                        <div>
                                            <h5>Credits:</h5>
                                            <ListGroup>
                                                {groupCredit.credits_per_user[
                                                    member.id
                                                ].map((user, index) => (
                                                    <ListGroup.Item key={index}>
                                                        {user.debtor.name} owes
                                                        you ${user.amount}
                                                    </ListGroup.Item>
                                                ))}
                                            </ListGroup>
                                        </div>
                                    )}
                                    {groupDebt.debts_per_user[member.id] && (
                                        <div>
                                            <h5>Debts:</h5>
                                            <ListGroup>
                                                {groupDebt.debts_per_user[
                                                    member.id
                                                ].map((user, index) => (
                                                    <ListGroup.Item key={index}>
                                                        You owe{" "}
                                                        {user.creditor.name} $
                                                        {user.amount}
                                                    </ListGroup.Item>
                                                ))}
                                            </ListGroup>
                                        </div>
                                    )}
                                </Accordion.Body>
                            </Accordion.Item>
                        ))}
                    </Accordion>
                </div>
            )}
        </div>
    );
}

export default GroupCreditDebit;
