import React, { useEffect, useState } from "react";
import { Modal, Button, FormControl, ListGroup } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

import { getGroupUserSearchToInvite } from "../../actions/groupActions";

function InviteUsersPopup({ show, onClose, groupId }) {
    const dispatch = useDispatch();
    const [searchTerm, setSearchTerm] = useState("");

    //groupUserSearchToInvite => store redcuer
    const groupUserSearchToInvite = useSelector(
        (state) => state.groupUserSearchToInvite
    );
    const {
        error: searchError,
        loading: searchLoading,
        userFriendNotMemberGroupSearchResults: searchResults,
    } = groupUserSearchToInvite;

    console.log(searchResults);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleClose = () => {
        setSearchTerm("");
        onClose();
    };

    useEffect(() => {
        dispatch(getGroupUserSearchToInvite(groupId, searchTerm));
    }, [dispatch, searchTerm]);

    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Header>
                <Modal.Title>Search Users</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <FormControl
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="mb-3"
                />
                {/* <ListGroup className="search-results-container">
                    {userFriendSearch &&
                        userFriendSearch.map((friend) => (
                            <ListGroup.Item key={friend.id}>
                                <div className="d-flex flex-row justify-content-between">
                                    <div className="d-flex align-items-center">
                                        {friend.email}
                                    </div>
                                    <Button
                                        onClick={() =>
                                            handleSendFriendRequest(friend.id)
                                        }
                                    >
                                        Send friend request
                                    </Button>
                                </div>
                            </ListGroup.Item>
                        ))}
                </ListGroup> */}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
            </Modal.Footer>
            {/* {showAlert && (
                <AlertMessage message={alertMessage} variant={alertVariant} />
            )} */}
        </Modal>
    );
}

export default InviteUsersPopup;
