import React, { useState } from "react";
import { Modal, Button, Tab, Nav } from "react-bootstrap";

function PendingInvitesPopup({ show, onClose }) {
    const [activeTab, setActiveTab] = useState("pendingFriendRequests");

    // Dummy data for pending friend requests and group requests
    const pendingFriendRequests = [
        { id: 1, name: "Friend 1" },
        { id: 2, name: "Friend 2" },
        // Add more friend requests
    ];

    const pendingGroupRequests = [
        { id: 1, name: "Group 1" },
        { id: 2, name: "Group 2" },
        // Add more group requests
    ];

    // Function to switch tabs
    const switchTab = (tabName) => {
        setActiveTab(tabName);
    };

    return (
        <div className={`pending-invites-popup ${show ? "active" : ""}`}>
            <div className="tabs">
                <button
                    className={`tab ${
                        activeTab === "pendingFriendRequests" ? "active" : ""
                    }`}
                    onClick={() => switchTab("pendingFriendRequests")}
                >
                    Pending Friend Requests
                </button>
                <button
                    className={`tab ${
                        activeTab === "pendingGroupRequests" ? "active" : ""
                    }`}
                    onClick={() => switchTab("pendingGroupRequests")}
                >
                    Pending Group Requests
                </button>
            </div>

            {/* Render the list based on the active tab */}
            {activeTab === "pendingFriendRequests" && (
                <ul className="list">
                    {pendingFriendRequests.map((request) => (
                        <li key={request.id}>
                            <span>{request.name}</span>
                            <button>Accept</button>
                            <button>Decline</button>
                        </li>
                    ))}
                </ul>
            )}

            {activeTab === "pendingGroupRequests" && (
                <ul className="list">
                    {pendingGroupRequests.map((request) => (
                        <li key={request.id}>
                            <span>{request.name}</span>
                            <button>Accept</button>
                            <button>Decline</button>
                        </li>
                    ))}
                </ul>
            )}

            <button onClick={onClose}>Close</button>
        </div>
    );
}

export default PendingInvitesPopup;
