import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { Badge, Row, Col, Button } from "react-bootstrap";

import "../../styles/screens/GroupsScreens.css";
import Message from "../Message";
import AlertMessage from "../AlertMessage";
import { getGroupExpenses } from "../../actions/groupActions";

function ExpenseList() {
    // group id
    const { id } = useParams();
    const dispatch = useDispatch();

    const groupExpenses = useSelector((state) => state.groupExpenses);
    const {
        error: groupExpensesError,
        loading: groupExpensesLoading,
        groupExpensesList,
    } = groupExpenses;

    console.log(groupExpensesList);

    // const [isGroupNameUpdated, setIsGroupNameUpdated] = useState(false);

    useEffect(() => {
        dispatch(getGroupExpenses(id));
    }, [dispatch, id]);

    return (
        <div className="expense-list">
            {groupExpensesLoading ? (
                <div>Loading...</div>
            ) : groupExpensesError ? (
                <Message variant="danger">{groupExpensesError}</Message>
            ) : groupExpensesList.length === 0 ? (
                <p>No expenses</p>
            ) : (
                <div className="border border-primary">
                    {groupExpensesList.map((expense) => (
                        <div key={expense.id} className="expense-row">
                            <Row className="d-flex flex-row justify-content-center p-0">
                                <Col
                                    md={1}
                                    className="expense-list-col d-flex flex-row align-items-center justify-content-center "
                                >
                                    <Badge pill bg="primary">
                                        Expense
                                    </Badge>
                                </Col>
                                <Col
                                    xs={2}
                                    md={1}
                                    className="expense-list-col d-flex flex-row align-items-center border border-primary"
                                >
                                    <span>{expense.payer.name}</span>
                                </Col>
                                <Col
                                    xs={4}
                                    sm={7}
                                    md={4}
                                    className="expense-list-col d-flex flex-row align-items-center border border-primary"
                                >
                                    <span>
                                        {expense.description.length > 20
                                            ? `${expense.description.substring(
                                                  0,
                                                  20
                                              )}...`
                                            : expense.description}
                                    </span>
                                </Col>
                                <Col
                                    xs={3}
                                    sm={1}
                                    md={2}
                                    className="expense-list-col d-flex flex-row align-items-center border border-primary"
                                >
                                    <span>${expense.amount}</span>
                                </Col>
                                <Col
                                    xs={3}
                                    sm={1}
                                    md={2}
                                    className="expense-list-col d-flex flex-row align-items-center border border-primary"
                                >
                                    <span>
                                        {new Date(
                                            expense.date
                                        ).toLocaleDateString()}
                                    </span>
                                </Col>
                                <Col
                                    sm={1}
                                    md={2}
                                    className="expense-list-col d-flex flex-row align-items-center justify-content-center py-2 border border-primary"
                                >
                                    <Button>edit</Button>
                                </Col>
                            </Row>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ExpenseList;
