import React from "react";
import { Modal, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";

import AlertMessage from "./AlertMessage";
import { deleteFriend } from "../actions/userActions";

function FriendOptionsPopup({
    show,
    onClose,
    onCloseFriendOptions,
    selectedFriend,
}) {
    const dispatch = useDispatch();

    const userFriendDeleteResults = useSelector(
        (state) => state.userFriendDelete
    );
    const { error, status } = userFriendDeleteResults;

    if (!show || selectedFriend === null) {
        // If show is false or selectedFriend is null, return null or handle accordingly
        return null;
    }

    const handleDeleteFriend = (id) => {
        console.log("deleting friend...");
        dispatch(deleteFriend(id));
        onCloseFriendOptions();
    };

    const handleAddToGroup = () => {
        console.log("add user to group");
        onCloseFriendOptions();
    };

    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Header closeButton>
                <Modal.Title>{selectedFriend.name.toUpperCase()}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Button
                    variant="danger"
                    onClick={() => handleDeleteFriend(selectedFriend.id)}
                    className="mx-1"
                >
                    Delete Friend
                </Button>
                <Button
                    variant="primary"
                    onClick={() => handleAddToGroup()}
                    className="mx-1"
                >
                    Add to Group
                </Button>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onClose}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
}

export default FriendOptionsPopup;
