import React, { useState } from "react";
import { Modal, Button, Tab, Nav } from "react-bootstrap";

import "../styles/screens/DashboardScreen.css";

function PendingInvitesPopup({ show, onClose }) {
    const [activeTab, setActiveTab] = useState("pendingFriendRequests");

    // Dummy data for pending friend requests and group requests
    const pendingFriendRequests = [
        { id: 1, name: "Friend 1" },
        { id: 2, name: "Friend 2" },
        // Add more friend requests
    ];

    const pendingGroupRequests = [
        { id: 1, name: "Group 1" },
        { id: 2, name: "Group 2" },
        // Add more group requests
    ];

    // Function to switch tabs
    // const switchTab = (tabName) => {
    //     setActiveTab(tabName);
    // };

    return (
        <Modal show={show} onHide={onClose}>
            <Modal.Body className="pending-invites-popup">
                <Tab.Container id="pending-invites-tabs" activeKey={activeTab}>
                    <Nav variant="tabs" className="mb-3">
                        <Nav.Item>
                            <Nav.Link
                                eventKey="pendingFriendRequests"
                                onClick={() =>
                                    setActiveTab("pendingFriendRequests")
                                }
                            >
                                Pending Friend Requests
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link
                                eventKey="pendingGroupRequests"
                                onClick={() =>
                                    setActiveTab("pendingGroupRequests")
                                }
                            >
                                Pending Group Requests
                            </Nav.Link>
                        </Nav.Item>
                    </Nav>
                    <Tab.Content>
                        <Tab.Pane eventKey="pendingFriendRequests">
                            <ul className="">
                                {pendingFriendRequests.map((request) => (
                                    <li key={request.id}>
                                        <div className="d-flex flex-row justify-content-between">
                                            <span>{request.name}</span>
                                            <div>
                                                <Button variant="success">
                                                    Accept
                                                </Button>
                                                <Button variant="danger">
                                                    Decline
                                                </Button>
                                            </div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </Tab.Pane>
                        <Tab.Pane eventKey="pendingGroupRequests">
                            <ul className="">
                                {pendingGroupRequests.map((request) => (
                                    <li key={request.id}>
                                        <span>{request.name}</span>
                                        <Button variant="success">
                                            Accept
                                        </Button>
                                        <Button variant="danger">
                                            Decline
                                        </Button>
                                    </li>
                                ))}
                            </ul>
                        </Tab.Pane>
                    </Tab.Content>
                </Tab.Container>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={onClose}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
}

export default PendingInvitesPopup;
